
public class LogicalOne {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		boolean a = (2 < 4);  //true
		boolean b = false;
		
		//logical and
		System.out.println("(a && b ) = " + (a && b));
		
		//logical or
		System.out.println("(a || b) = " + (a || b));
		
		//logical not
		System.out.println("!(a) = " + !(a));
		System.out.println("!(b) = " + !(b));
		System.out.println("!(a && b) = " + !(a && b));
		
	}

}
